onLogIn: async () => {
                    if((email.text !== '') && (password.text !=='')){
                        try{ 
                                    await sign_in.run() ;
                                                        const jwt = sign_in?.data?.access_token;
                                                        const user = sign_in?.data?.user;
                                                                    if(jwt) {
                                                                            storeValue('jwt', jwt);
