                                                                            navigateTo('Home', {})
                                                                                    }
                                    } catch (error) {
                                                                                    showAlert('Your email and/or password do not match.', 'error')
                                                                            }
                        }else{
                                        showAlert('âŒ Please fill out the information completely.')
                                        }
    } ,
	continue: async () => {
		if(!appsmith.URL.fullPath.includes('#access_token=')) return;	
		appsmith.URL.fullPath.split('#') [1].split('&').forEach(i => {
			const [key, value] =i.split('=');
			storeValue(key,  value);
			//const user = get_user.data?.user;
			//storeValue('user', user);
		});
		navigateTo('Home');
	}
}
